import React, { useRef, useState } from "react";
import classes from "./FormOrder.module.css";

const FormOrder = (props) => {
  const [name, setName] = useState("");
  const [address, setAddress] = useState("");
  const [city, setCity] = useState("");
  const [zip, setZip] = useState("");
  const [message, setMessage] = useState("");
  const [error, setError] = useState(["name", "address", "ZIP code", "city"]);

  const nameHandler = (e) => {
    if (e.target.value !== "") {
      setName(e.target.value);
      setError((prev) => prev.filter((type) => type !== "name"));
    }
  };
  const addressHandler = (e) => {
    if (e.target.value !== "") {
      setAddress(e.target.value);
    }
    setError((prev) => prev.filter((type) => type !== "address"));
  };

  const zipHandler = (e) => {
    if (e.target.value !== "") {
      setZip(e.target.value);
    }
    setError((prev) => prev.filter((type) => type !== "ZIP code"));
  };

  const cityHandler = (e) => {
    if (e.target.value !== "") {
      setCity(e.target.value);
    }
    setError((prev) => prev.filter((type) => type !== "city"));
  };

  const submitHandler = (e) => {
    e.preventDefault();
    if (error.length === 0) {
      console.log(error);
      var orderInfo = {
        name: name,
        address: address,
        city: city,
        zip: zip,
      };
      console.log(orderInfo);
      props.onAddMeal(orderInfo);
      setName("");
      setAddress("");
      setCity("");
      setZip("");
      setMessage("");
    } else {
      setMessage(
        "The following fields are incorrect:" + error.map((er) => " " + er)
      );
    }
  };

  return (
    <>
      <h2 className={classes.titleForm}>Insert you info</h2>
      <div className={classes.message}>{message}</div>
      <form className={classes.formOrder} onSubmit={submitHandler}>
        <input
          type="text"
          placeholder="Name..."
          onChange={nameHandler}
          value={name}
        ></input>

        <input
          type="text"
          placeholder="Address..."
          onChange={addressHandler}
          value={address}
        ></input>

        <input
          type="text"
          placeholder="City..."
          onChange={cityHandler}
          value={city}
        ></input>

        <input
          type="text"
          placeholder="ZIP Code..."
          onChange={zipHandler}
          value={zip}
        ></input>

        <div className={classes.buttonOrderContainer}>
          <button className={classes.buttonCancel}>Cancel</button>
          <button type="submit" className={classes.buttonForm}>
            Complete order
          </button>
        </div>
      </form>
    </>
  );
};

export default FormOrder;
