import React, { useRef } from "react";
import classes from "./FormOrder.module.css";

const FormOrder = (props) => {
  const nameRef = useRef("");
  const surnameRef = useRef("");
  const addressRef = useRef("");
  const cityRef = useRef("");
  const zipRef = useRef("");
  const emailRef = useRef("");

  const submitHandler = (e) => {
    e.preventDefault();
    if (
      nameRef.current.value.trim().lenght() > 0 &&
      surnameRef.current.value.trim().lenght() > 0 &&
      addressRef.current.value.trim().lenght() > 0 &&
      cityRef.current.value.trim().lenght() > 0 &&
      zipRef.current.value.trim().lenght() > 0 &&
      addressRef.current.value.trim().lenght() > 0
    ) {
      var orderInfo = {
        name: nameRef.current.value,
        surname: surnameRef.current.value,
        address: addressRef.current.value,
        city: cityRef.current.value,
        zip: zipRef.current.value,
        email: addressRef.current.value,
      };
    } else {
      alert("Dati inseriti non corretti!");
    }
    props.onAddMeal(orderInfo);
  };

  return (
    <>
      <h2 className={classes.titleForm}>Insert you info</h2>
      <form className={classes.formOrder} onSubmit={submitHandler}>
        <input
          type="text"
          placeholder="Name..."
          ref={nameRef}
          onChange={nameHandler}
        ></input>

        <input
          type="text"
          placeholder="Surname..."
          ref={surnameRef}
          onChange={surnameHandler}
        ></input>
        <input
          type="email"
          placeholder="Email..."
          ref={emailRef}
          onChange={emailHandler}
        ></input>

        <input
          type="text"
          placeholder="Address..."
          ref={addressRef}
          onChange={addressHandler}
        ></input>

        <input
          type="text"
          placeholder="City..."
          ref={cityRef}
          onChange={cityHandler}
        ></input>

        <input
          type="text"
          placeholder="ZIP Code..."
          ref={zipRef}
          onChange={zipHandler}
        ></input>

        <div className={classes.buttonOrderContainer}>
          <button type="submit" className={classes.buttonForm}>
            Complete order
          </button>
        </div>
      </form>
    </>
  );
};

export default FormOrder;
