import React, { useRef, useState } from "react";
import classes from "./FormOrder.module.css";

const FormOrder = (props) => {
  const [name, setName] = useState("");
  const [nameIsValid, setNameIsValid] = useState(false);
  const [address, setAddress] = useState("");
  const [addressIsValid, setAddressIsValid] = useState(false);
  const [city, setCity] = useState("");
  const [cityIsValid, setCityIsValid] = useState(false);
  const [zip, setZip] = useState("");
  const [zipIsValid, setZipIsValid] = useState(false);
  const [message, setMessage] = useState(false);

  const nameHandler = (e) => {
    setName(e.target.value);
    if (e.target.value.trim().length > 0) {
      setNameIsValid(true);
    } else {
      setNameIsValid(false);
    }
  };
  const addressHandler = (e) => {
    setAddress(e.target.value);
    if (e.target.value.trim().length > 0) {
      setAddressIsValid(true);
    } else {
      setAddressIsValid(false);
    }
  };

  const zipHandler = (e) => {
    setZip(e.target.value);
    if (e.target.value.trim().length > 0) {
      setZipIsValid(true);
    } else {
      setZipIsValid(false);
    }
  };

  const cityHandler = (e) => {
    setCity(e.target.value);
    if (e.target.value.trim().length > 0) {
      setCityIsValid(true);
    } else {
      setCityIsValid(false);
    }
  };

  const submitHandler = (e) => {
    e.preventDefault();
    if (nameIsValid && addressIsValid && zipIsValid && cityIsValid) {
      var orderInfo = {
        name: name,
        address: address,
        city: city,
        zip: zip,
      };
      console.log(orderInfo);
      props.onAddMeal(orderInfo);
      setName("");
      setAddress("");
      setCity("");
      setZip("");
      setMessage("");
    } else {
      setMessage(true);
    }
  };

  return (
    <>
      <h2 className={classes.titleForm}>Insert you info</h2>
      <div className={classes.message}>{message}</div>
      <form className={classes.formOrder} onSubmit={submitHandler}>
        <div className={inputContainer}>
          <input
            type="text"
            placeholder="Name..."
            onChange={nameHandler}
            value={name}
          ></input>
          {message === true && nameIsValid === false ? (
            <p> Insert a valid name </p>
          ) : (
            ""
          )}
        </div>
        <div className={inputContainer}>
          <input
            type="text"
            placeholder="Address..."
            onChange={addressHandler}
            value={address}
          ></input>
          {message === true && addressIsValid === false ? (
            <p> Insert a valid address </p>
          ) : (
            ""
          )}
        </div>
        <div className={inputContainer}>
          <input
            type="text"
            placeholder="City..."
            onChange={cityHandler}
            value={city}
          ></input>
          {message === true && cityIsValid === false ? (
            <p> Insert a valid city </p>
          ) : (
            ""
          )}
        </div>
        <div className={inputContainer}>
          <input
            type="text"
            placeholder="ZIP Code..."
            onChange={zipHandler}
            value={zip}
          ></input>
          {message === true && zipIsValid === false ? (
            <p> Insert a valid ZIP code </p>
          ) : (
            ""
          )}
        </div>

        <div className={classes.buttonOrderContainer}>
          <button className={classes.buttonCancel}>Cancel</button>
          <button type="submit" className={classes.buttonForm}>
            Complete order
          </button>
        </div>
      </form>
    </>
  );
};

export default FormOrder;
