import { useContext, useState } from "react";
import CartContext from "../../store/cart-context";
import Modal from "../UI/Modal";
import CartItem from "./CartItem";
import FormOrder from "./FormOrder";
import classes from "./Cart.module.css";

const Cart = (props) => {
  const [confirmOrder, setConfirmOrder] = useState(false);
  const cartCtx = useContext(CartContext);
  const totalAmount = `€ ${cartCtx.totalAmount.toFixed(2)}`;
  const hasItems = cartCtx.items.length > 0;
  const cartItemRemoveHandler = (id) => {
    cartCtx.removeItem(id);
  };
  const cartItemAddHandler = (item) => {
    cartCtx.addItem({ ...item, amount: 1 });
  };
  const cartItems = (
    <ul className={classes["cart-items"]}>
      {cartCtx.items.map((item) => (
        <CartItem
          key={item.id}
          name={item.name}
          amount={item.amount}
          price={item.price}
          onRemove={cartItemRemoveHandler.bind(null, item.id)}
          onAdd={cartItemAddHandler.bind(null, item)}
        />
      ))}
      {console.log(cartItems)}
    </ul>
  );
  const onOrder = () => {
    setConfirmOrder(!confirmOrder);
  };

  async function addMealHandler(orderInfo) {
    const finalOrder = [...orderInfo, ...cartCtx];
    const response = await fetch(
      "https://order-food-app-25b86-default-rtdb.firebaseio.com/orders.json",
      {
        method: "POST",
        body: JSON.stringify(finalOrder),
        headers: { "Content-type": "application/json" },
      }
    );
    const data = await response.json();
    console.log(data);
  }
  return (
    <Modal onClick={props.onClose}>
      {cartItems}
      <div className={classes.total}>
        <span>Total Amount</span>
        <span>{totalAmount}</span>
      </div>
      <div className={classes.actions}>
        <button className={classes["button--alt"]} onClick={props.onClose}>
          Close
        </button>
        {hasItems && (
          <button className={classes.button} onClick={onOrder}>
            Order
          </button>
        )}
        {confirmOrder && <FormOrder onAddMeal={addMealHandler} />}
      </div>
    </Modal>
  );
};

export default Cart;
