import React, { useRef } from "react";
import classes from "./FormOrder.module.css";

const FormOrder = (props) => {
  const nameRef = useRef("");
  const surnameRef = useRef("");
  const addressRef = useRef("");
  const cityRef = useRef("");
  const zipRef = useRef("");
  const emailRef = useRef("");

  const submitHandler = (e) => {
    e.preventDefault();
    var orderInfo = {
      name: nameRef.current.value,
      surname: surnameRef.current.value,
      address: addressRef.current.value,
      city: cityRef.current.value,
      zip: zipRef.current.value,
      email: addressRef.current.value,
    };
    props.onAddMeal(orderInfo);
  };

  return (
    <form className={classes.formOrder} onSubmit={submitHandler}>
      <label>Name</label>
      <input type="text" placeholder="Name..." ref={nameRef}></input>
      <label>Surname</label>
      <input type="text" ref={surnameRef}></input>
      <label>Address</label>
      <input type="text" ref={addressRef}></input>
      <label>City</label>
      <input type="text" ref={cityRef}></input>
      <label>ZIP Code</label>
      <input type="text" ref={zipRef}></input>
      <label>E-mail</label>
      <input type="email" ref={emailRef}></input>
      <button type="submit">Complete order</button>
    </form>
  );
};

export default FormOrder;
