import React, { useRef, useState } from "react";
import classes from "./FormOrder.module.css";

const FormOrder = (props) => {
  const [name, setName] = useState("");
  const [surname, setSurname] = useState("");
  const [address, setAddress] = useState("");
  const [city, setCity] = useState("");
  const [zip, setZip] = useState("");
  const [email, setEmail] = useState("");
  const [form, setForm] = useState("");
  const [error, setError] = useState([]);
  /*const nameRef = useRef("");
  const surnameRef = useRef("");
  const addressRef = useRef("");
  const cityRef = useRef("");
  const zipRef = useRef("");
  const emailRef = useRef("");*/

  const nameHandler = (e) => {
    if (e.target.value.trim().length() > 0) {
      setName(e.target.value);
    } else {
      setError((prev) => [...prev].push("name"));
    }
  };
  const surnameHandler = (e) => {
    if (e.target.value.trim().length() > 0) {
      setSurname(e.target.value);
    } else {
      setError((prev) => [...prev].push("surname"));
    }
  };
  const addressHandler = (e) => {
    if (e.target.value.trim().length() > 0) {
      setAddress(e.target.value);
    } else {
      setError((prev) => [...prev].push("surname"));
    }
  };

  const zipHandler = (e) => {
    if (e.target.value.trim().length() > 0) {
      setZip(e.target.value);
    } else {
      setError((prev) => [...prev].push("ZIP code"));
    }
  };

  const cityHandler = (e) => {
    if (e.target.value.trim().length() > 0) {
      setCity(e.target.value);
    } else {
      setError((prev) => [...prev].push("city"));
    }
  };
  const emailHandler = (e) => {
    if (e.target.value.trim().length() > 0) {
      setEmail(e.target.value);
    } else {
      setError((prev) => [...prev].push("email"));
    }
  };

  const submitHandler = (e) => {
    e.preventDefault();
    /* var orderInfo = {
      name: nameRef.current.value,
      surname: surnameRef.current.value,
      address: addressRef.current.value,
      city: cityRef.current.value,
      zip: zipRef.current.value,
      email: addressRef.current.value,
    };*/
    props.onAddMeal(orderInfo);
  };

  return (
    <>
      <h2 className={classes.titleForm}>Insert you info</h2>
      <form className={classes.formOrder} onSubmit={submitHandler}>
        <input
          type="text"
          placeholder="Name..."
          ref={nameRef}
          onChange={nameHandler}
        ></input>

        <input
          type="text"
          placeholder="Surname..."
          ref={surnameRef}
          onChange={surnameHandler}
        ></input>
        <input
          type="email"
          placeholder="Email..."
          ref={emailRef}
          onChange={emailHandler}
        ></input>

        <input
          type="text"
          placeholder="Address..."
          ref={addressRef}
          onChange={addressHandler}
        ></input>

        <input
          type="text"
          placeholder="City..."
          ref={cityRef}
          onChange={cityHandler}
        ></input>

        <input
          type="text"
          placeholder="ZIP Code..."
          ref={zipRef}
          onChange={zipHandler}
        ></input>

        <div className={classes.buttonOrderContainer}>
          <button type="submit" className={classes.buttonForm}>
            Complete order
          </button>
        </div>
      </form>
    </>
  );
};

export default FormOrder;
