import React, { useRef, useState } from "react";
import classes from "./FormOrder.module.css";

const FormOrder = (props) => {
  const [name, setName] = useState("");
  const [surname, setSurname] = useState("");
  const [address, setAddress] = useState("");
  const [city, setCity] = useState("");
  const [zip, setZip] = useState("");
  const [email, setEmail] = useState("");
  const [form, setForm] = useState(false);
  const [error, setError] = useState([]);

  const nameHandler = (e) => {
    if (e.target.value.trim().length > 0) {
      setName(e.target.value);
    } else {
      setError((prev) => [...prev].push("name"));
    }
  };
  const surnameHandler = (e) => {
    if (e.target.value.trim().length > 0) {
      setSurname(e.target.value);
    } else {
      setError((prev) => [...prev].push("surname"));
    }
  };
  const addressHandler = (e) => {
    if (e.target.value.trim().length > 0) {
      setAddress(e.target.value);
    } else {
      setError((prev) => [...prev].push("surname"));
    }
  };

  const zipHandler = (e) => {
    if (e.target.value.trim().length > 0) {
      setZip(e.target.value);
    } else {
      setError((prev) => [...prev].push("ZIP code"));
    }
  };

  const cityHandler = (e) => {
    if (e.target.value.trim().length > 0) {
      setCity(e.target.value);
    } else {
      setError((prev) => [...prev].push("city"));
    }
  };
  const emailHandler = (e) => {
    if (e.target.value.trim().length > 0) {
      setEmail(e.target.value);
    } else {
      setError((prev) => [...prev].push("email"));
    }
  };

  const submitHandler = (e) => {
    e.preventDefault();
    if (error != []) {
      console.log(error);
      var orderInfo = {
        name: name,
        surname: surname,
        address: address,
        city: city,
        zip: zip,
        email: address,
      };
      console.log(orderInfo);
      props.onAddMeal(orderInfo);
    } else {
      alert(error);
    }
  };

  return (
    <>
      <h2 className={classes.titleForm}>Insert you info</h2>
      <form className={classes.formOrder} onSubmit={submitHandler}>
        <input type="text" placeholder="Name..." onChange={nameHandler}></input>

        <input
          type="text"
          placeholder="Surname..."
          onChange={surnameHandler}
        ></input>
        <input
          type="email"
          placeholder="Email..."
          onChange={emailHandler}
        ></input>

        <input
          type="text"
          placeholder="Address..."
          onChange={addressHandler}
        ></input>

        <input type="text" placeholder="City..." onChange={cityHandler}></input>

        <input
          type="text"
          placeholder="ZIP Code..."
          onChange={zipHandler}
        ></input>

        <div className={classes.buttonOrderContainer}>
          <button type="submit" className={classes.buttonForm}>
            Complete order
          </button>
        </div>
      </form>
    </>
  );
};

export default FormOrder;
